document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("bubbleContainer");if(!t)return;const e=t.querySelectorAll(".bubble-item");if(e.length===0)return;e.forEach(e=>{e.physics={ampX1:Math.random()*20+10,ampX2:Math.random()*10+5,ampY1:Math.random()*25+10,ampY2:Math.random()*15+5,freqX1:Math.random()*.001+5e-4,freqX2:Math.random()*.002+.001,freqY1:Math.random()*.001+5e-4,freqY2:Math.random()*.002+.001,phaseX1:Math.random()*Math.PI*2,phaseX2:Math.random()*Math.PI*2,phaseY1:Math.random()*Math.PI*2,phaseY2:Math.random()*Math.PI*2,isHovering:!1,scale:1,baseZIndex:1},e.addEventListener("mouseenter",()=>{e.physics.isHovering=!0,e.physics.scale=1.15,e.style.zIndex=100}),e.addEventListener("mouseleave",()=>{e.physics.isHovering=!1,e.physics.scale=1,setTimeout(()=>{e.physics.isHovering||(e.style.zIndex=e.physics.baseZIndex)},300)})}),startAnimationLoop(e)});function startAnimationLoop(e){function t(n){const s=n;e.forEach(e=>{const t=e.physics,n=t.ampX1*Math.sin(s*t.freqX1+t.phaseX1)+t.ampX2*Math.cos(s*t.freqX2+t.phaseX2),o=t.ampY1*Math.sin(s*t.freqY1+t.phaseY1)+t.ampY2*Math.cos(s*t.freqY2+t.phaseY2),i=t.isHovering?-15:0,a=n*.2;e.style.transform=`translate3d(${n.toFixed(2)}px, ${(o+i).toFixed(2)}px, 0) scale(${t.scale}) rotate(${a.toFixed(2)}deg)`}),requestAnimationFrame(t)}requestAnimationFrame(t)}